import React, {useState} from 'react';
import { useNavigation } from '@react-navigation/core';
import { StyleSheet, View, Image, Text, TouchableOpacity, TextInput, Platform} from 'react-native';
import DataTable, {createTheme} from "react-data-table-component";
import {Searchbar} from 'react-native-paper'
import NavBar from '../../components/NavBar'
import ImageGiant from '../../components/ImageGiant'
import movies from '../../Data/Musics/music'
const columns = [
    {
      name: "Nome",
      selector: "nome",
      sortable: true,
      backgroundColor:'#E3E1E1',
      center:true
    },
    {
      name: "Artista",
      selector: "artista",
      sortable: true,
      center:true
    },
    {
      name: "Reproduções",
      selector: "vezesTocadas",
      sortable: true,
      center:true
    },
    {
      name: "Duração",
      selector: "duração",
      sortable: true,
      center:true
    },
    {
      name: "Descrição",
      selector: "descrição",
      sortable: true,
      center:true
    }

  ];

export default function Home(){
    const [searchQuery, setSearchQuery] = React.useState('');
    const onChangeSearch = query => setSearchQuery(query);
    return(
        <View style={{flex:1}}>
            <View>
                <NavBar/>
            </View>
            <View style = {{flex:1 ,backgroundColor:"#F3F3F3",flexDirection:'row'}}>
                <View style = {{flex:1}}>
                    <ImageGiant/>
                </View>
                <View style = {{flex:4, flexDirection:'column', paddingTop:'2%'}}>
                        <View style={{alignItems:'center'}}>
                            <Searchbar
                                placeholder="Procure músicas"
                                onChangeText={onChangeSearch}
                                value={searchQuery}
                                style ={{borderRadius:10, width:'100%'}}
                            />
                            <Text style = {{fontSize:30, fontWeight:'bold', color:'#3E6097', textAlign:'center', paddingTop:20}}>Suas Músicas Mais Tocadas</Text>
                            <View style={{paddingTop:20}}></View>
                            <Text style = {{fontSize:18, color:'#374754', textAlign:'center'}}>Selecionamos as músicas que você mais escutou nos últimos dias. Escute, agora mesmo, suas músicas do momento!</Text>
                        </View>
                        <View style={{paddingTop:40}}></View>
                        <View style = {{flex:5,borderTopRightRadius:20,borderTopLeftRadius:20}}>           
                            <DataTable
                                columns={columns}
                                data={movies}
                                pagination
                                selectableRows
                                noHeader={true}
                                theme="SpotPer"
                                selectableRowsHighlight={true}
                                customStyles={customStyles}
                            />
                        </View>
                </View>
                <View style={{flex:1}}/>
                {/*<View style = {{flex:1,flexDirection:'column', justifyContent:'space-between'}}>
                    <View style= {{flex:1, alignItems:'flex-end', justifyContent:'center', paddingRight:10}}>
                        <Searchbar
                            placeholder="Procure músicas"
                            onChangeText={onChangeSearch}
                            value={searchQuery}
                            style ={{borderRadius:10, width:'80%'}}
                        />
                        </View>
                        <View style= {{flex:2, alignItems:'flex-end',paddingTop:'100%'}}>
                            <Image source = {require('../../../assets/homeImage.png')} style ={styles.image}/>
                    </View>
                </View>*/}
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    ...Platform.select({
        ios:{
        },
        android:{
        },
        web:{
            image:{
                flex:1,
                width: "70%",
                resizeMode:'contain',
            }
        }
    })
});

createTheme('SpotPer', {
    text: {
      primary: '#374754',
      secondary: '#374754',
    },
    background: {
      default: '#E3E1E1',
    },
    context: {
      background: '#cb4b16',
      text: '#FFFFFF',
    },
    divider: {
      default: '#073642',
    }
  });

  const customStyles = {
    rows: {
      style: {
        minHeight: '55px'
      }
    },
    headCells: {
      style: {
        paddingLeft:'30px'
      },
    },
    cells: {
      style: {
        paddingLeft: '8px',
        paddingRight: '8px'
      },
    },
  };
