import React, {useState} from 'react';
import {View,Text, Platform, Image, TouchableOpacity} from 'react-native';
import DataTable, {createTheme} from "react-data-table-component";
import {Searchbar} from 'react-native-paper'
import NavBar from '../../components/NavBar'
import movies from '../../data/TableHome/Musics/music'
import columns from '../../data/TableHome/Header/header'
import styleWeb from '../../styles/web/AllMusics/style'


export default function AllMusics(){
  const [searchQuery, setSearchQuery] = useState('');
  const onChangeSearch = query => setSearchQuery(query);

  /*{Web app}*/
  if(Platform.OS === 'web'){
    return(
      <View style={styleWeb.navbar}>
        <View>
          <NavBar/>
        </View>
        <View style = {styleWeb.container}>
          <View style = {styleWeb.imagegiant}>
            <View style= {styleWeb.containerimage}>
               <Image source = {require('../../../assets/imageGiantAllMusics.png')} style ={styleWeb.image}/>
            </View>
          </View>
          <View style = {styleWeb.home}>
            <View style={styleWeb.containertext}>
              <Text style = {styleWeb.title}>Suas Faixas - Visão Geral</Text>
              <View style={{paddingTop:20}}></View>
              <Text style = {styleWeb.description}>Aqui está a seleção de todas as suas músicas!</Text>
              <Text style = {styleWeb.description}>Dica: Crie uma playlist para organizar a visualização das suas músicas.</Text>
              <View style={{paddingTop:20}}></View>
              <View style={styleWeb.containersearch}>
                <View style={styleWeb.searchbar}>
                  <Searchbar
                    placeholder="Procure músicas"
                    onChangeText={onChangeSearch}
                    value={searchQuery}
                    style ={{borderRadius:10}}
                  />

                </View>
              </View>
            </View>
            <View style = {styleWeb.table}>           
              <DataTable
                columns={columns}
                data={movies}
                pagination
                selectableRows
                noHeader={true}
                theme="SpotPer"
                selectableRowsHighlight={true}
                customStyles={customStyles}
              />
            </View>
          </View>
          <View style={{flex:0.5}}>
          <View style={{flex:1, flexDirection:'column', paddingTop:'87%', backgroundColor:'black'}}>
                    <TouchableOpacity>
                        <View style={{backgroundColor:'#3E6097', padding:10, borderRadius:20, paddingLeft:10,width:'90%'}}>
                            <Text style={{fontSize:18, color:'#C4C4C4', textAlign:'center'}}>Adicionar Música</Text>
                        </View>
                    </TouchableOpacity>
                    <View style={{paddingTop:20}}/>
                    <TouchableOpacity>
                        <View style={{backgroundColor:'#3E6097', padding:10, borderRadius:20, width:'100%'}}>
                            <Text style={{fontSize:18, color:'#760000', textAlign:'center'}}>Excluir Música</Text>
                        </View>
                    </TouchableOpacity>
              </View>
          </View>
        </View>
      </View>
    );
  }
  /*{Mobile app}*/
  else if(Platform.OS === 'android' || Platform.OS === 'ios'){
  }
}

/*{Data style}*/
createTheme('SpotPer', {
  text: {
    primary: '#374754',
    secondary: '#374754',
  },
  background: {
    default: '#E3E1E1',
  },
  context: {
    background: '#cb4b16',
    text: '#FFFFFF',
  },
  divider: {
    default: '#073642',
  }
});

const customStyles = {
  rows: {
    style: {
      minHeight: '55px'
    }
  },
  headCells: {
    style: {
      paddingLeft:'30px'
    },
  },
  cells: {
    style: {
      paddingLeft: '8px',
      paddingRight: '8px'
    },
  },
};
