import axios from 'axios'

const api = axios.create({
    baseURL: 'http://localhost:3000/'
})

const getUser = (email, res, err)=>{
    api.get('user', {email: email}).then(res, err)
}

const registerUser = (email, username, password, res, err)=>{
    api.post('register', {
        email: email,
        username: username,
        password: password
    }).then(res, err)
}

export {
    api,
    getUser,
    registerUser
}
