import React from 'react';
import {StyleSheet, View, Image, Text,TouchableOpacity, Platform,StatusBar,ScrollView} from 'react-native';
import styleWeb from '../../styles/web/NavBar/style'
import styleIOS from '../../styles/iOS/NavBar/style'
export default function NavBar(){

    /*{Web app}*/
    if(Platform.OS === 'web'){
        return(
            <View style= {styleWeb.container}>
                <View style={styleWeb.components}>
                    <View style={{paddingLeft:10}}>
                        <Image source = {require('../../../assets/logo.png')} style ={styleWeb.imagelogo}/>
                    </View>
                    <Text style={styleWeb.textlogo}>SpotPer</Text>
                </View>
                <View style={styleWeb.components}>
                    <View style ={styleWeb.options}>
                        <Image source = {require('../../../assets/home.png')} style ={styleWeb.icons}/>
                        <Text style={styleWeb.textActive}>Home</Text>
                    </View>
                    <Text style = {{fontSize:20}}>|</Text>
                    <View style={{paddingLeft:10}}/>
                    <View style ={styleWeb.options}>
                        <Image source = {require('../../../assets/faixas.png')} style ={styleWeb.icons}/>
                        <Text style={styleWeb.textDeactive}>Suas Faixas</Text>
                    </View>
                    <View style={{paddingLeft:10}}/>
                    <Text style = {{fontSize:20}}>|</Text>
                    <View style={{paddingLeft:10}}/>
                    <View style ={styleWeb.options}>
                        <Image source = {require('../../../assets/playlists.png')} style ={styleWeb.icons}/>
                        <Text style={styleWeb.textDeactive}>Suas Playlists</Text>
                    </View>
                    <View style={{paddingLeft:10}}/>
                    <Text style = {{fontSize:20}}>|</Text>
                    <View style={{paddingLeft:10}}/>
                    <View style ={styleWeb.options}>
                        <Image source = {require('../../../assets/artistas.png')} style ={styleWeb.icons}/>
                        <Text style={styleWeb.textDeactive}>Artistas</Text>
                    </View>
                </View>
                <View style={styleWeb.profile}>
                    <View>
                        <TouchableOpacity>
                            <Image source = {require('../../../assets/profile.png')} style ={styleWeb.profileicon}/>
                        </TouchableOpacity>
                    </View>
                </View>
            </View>
        );
    }

    /*{Android app}*/
    else if(Platform.OS === 'android' || Platform.OS === 'ios'){
        return(
            <View>
                <StatusBar backgroundColor="#D8CECE"/>
                <ScrollView style={{width:"100%", height:"100%"}}>
                    <View style= {styles.container}>
                        <View style={styles.components}>
                            <View style={{paddingLeft:10}}>
                                <Image source = {require('../../../assets/logo.png')} style ={{width:60,height:60}}/>
                            </View>
                            <Text style={styles.textlogo}>SpotPer</Text>
                        </View>
                        <View style={styles.profile}>
                            <View>
                                <TouchableOpacity>
                                    <Image source = {require('../../../assets/profile.png')} style ={{width:40,height:40}}/>
                                </TouchableOpacity>
                            </View>
                        </View>
                    </View>
                </ScrollView>
            </View>
        );
    }

     /*{ios app}*/
     else if(Platform.OS === 'android' || Platform.OS === 'ios'){
        return(
            <View>
                <StatusBar backgroundColor="#D8CECE"/>
                <ScrollView style={{width:"100%", height:"100%"}}>
                    <View style= {styleIOS.container}>
                        <View style={styleIOS.components}>
                            <View style={{paddingLeft:10}}>
                                <Image source = {require('../../../assets/logo.png')} style ={{width:60,height:60}}/>
                            </View>
                            <Text style={styleIOS.textlogo}>SpotPer</Text>
                        </View>
                        <View style={styleIOS.profile}>
                            <View>
                                <TouchableOpacity>
                                    <Image source = {require('../../../assets/profile.png')} style ={{width:40,height:40}}/>
                                </TouchableOpacity>
                            </View>
                        </View>
                    </View>
                </ScrollView>
            </View>
        );
    }
}

const styles = StyleSheet.create({
    ...Platform.select({
        ios:{
            container:{
                paddingTop:40,
                flexDirection:'row',
                justifyContent:'space-between',
                backgroundColor:"#D8CECE",
                alignItems:'center'
            },
            components:{
                flexDirection:'row',
                 alignItems:'center',
                  width:"33%"
            },
            options:{
                flexDirection:'column',
                alignItems:'center',
                width:"25%",
                justifyContent:'center'
            },
            textlogo:{
                color:'#3E6097',
                fontWeight:'bold',
                fontSize:30
            },
            textActive:{
                color:"#3E6097",
                fontWeight:'bold',
                textAlign:'center'
            },
            textDeactive:{
                color:"#374754",
                fontWeight:'bold',
                textAlign:'center'
            },
            profile:{
                paddingRight:10,
                width:"33%",
                alignItems:'flex-end'
            }
        },
        android:{
            container:{
                flexDirection:'row',
                justifyContent:'space-between',
                backgroundColor:"#D8CECE",
                alignItems:'center'
            },
            components:{
                flexDirection:'row',
                 alignItems:'center',
                  width:"33%"
            },
            options:{
                flexDirection:'column',
                alignItems:'center',
                width:"25%",
                justifyContent:'center'
            },
            textlogo:{
                color:'#3E6097',
                fontWeight:'bold',
                fontSize:30
            },
            textActive:{
                color:"#3E6097",
                fontWeight:'bold',
                textAlign:'center'
            },
            textDeactive:{
                color:"#374754",
                fontWeight:'bold',
                textAlign:'center'
            },
            profile:{
                paddingRight:10,
                width:"33%",
                alignItems:'flex-end'
            }
        }
    })
});
