import React, {useState} from 'react';
import { StyleSheet, View,Text, Platform} from 'react-native';
import DataTable, {createTheme} from "react-data-table-component";
import {Searchbar} from 'react-native-paper'
import NavBar from '../../components/NavBar'
import ImageGiant from '../../components/ImageGiant'
import movies from '../../data/TableHome/Musics/music'
import columns from '../../data/TableHome/Header/header'

export default function Home(){
    const [searchQuery, setSearchQuery] = useState('');
    const onChangeSearch = query => setSearchQuery(query);
    return(
        <View style={{flex:1}}>
            <View>
                <NavBar/>
            </View>
            <View style = {{flex:1 ,backgroundColor:"#F3F3F3",flexDirection:'row', paddingRight:50}}>
                <View style = {{flex:1}}>
                    <ImageGiant/>
                </View>
                <View style = {{flex:4, flexDirection:'column', paddingTop:'2%', paddingLeft:50}}>
                    <View style={{alignItems:'center'}}>
                        <Text style = {{fontSize:30, fontWeight:'bold', color:'#3E6097', textAlign:'center', paddingTop:20}}>Suas Músicas Mais Tocadas</Text>
                        <View style={{paddingTop:20}}></View>
                        <Text style = {{fontSize:18, color:'#374754', textAlign:'center'}}>Selecionamos as músicas que você mais escutou nos últimos dias. Escute, agora mesmo, suas músicas do momento!</Text>
                        <View style={{paddingTop:20}}></View>
                        <View style={{backgroundColor:'#E3E1E1', width:'100%',borderTopRightRadius:20,borderTopLeftRadius:20}}>
                            <View style={{padding:10, alignItems:'flex-end',paddingRight:25}}>
                                <Searchbar
                                    placeholder="Procure músicas"
                                    onChangeText={onChangeSearch}
                                    value={searchQuery}
                                    style ={{borderRadius:10}}
                                />
                            </View>
                        </View>
                    </View>
                    <View style = {{flex:5}}>           
                        <DataTable
                            columns={columns}
                            data={movies}
                            pagination
                            selectableRows
                            noHeader={true}
                            theme="SpotPer"
                            selectableRowsHighlight={true}
                            customStyles={customStyles}
                        />
                    </View>
                </View>
            </View>
        </View>
    );
}

createTheme('SpotPer', {
    text: {
      primary: '#374754',
      secondary: '#374754',
    },
    background: {
      default: '#E3E1E1',
    },
    context: {
      background: '#cb4b16',
      text: '#FFFFFF',
    },
    divider: {
      default: '#073642',
    }
  });

  const customStyles = {
    rows: {
      style: {
        minHeight: '55px'
      }
    },
    headCells: {
      style: {
        paddingLeft:'30px'
      },
    },
    cells: {
      style: {
        paddingLeft: '8px',
        paddingRight: '8px'
      },
    },
  };
