import React, {useState} from 'react';
import {View,Text, Platform,TouchableOpacity, Image, ImageBackground} from 'react-native';
import DataTable, {createTheme} from "react-data-table-component";
import {Searchbar} from 'react-native-paper'
import {Audio} from 'expo-av'
import NavBar from '../../components/NavBar/NavBarHome'
import ImageGiant from '../../components/ImageGiant'
import movies from '../../data/TableHome/Musics/music'
import columns from '../../data/TableHome/Header/header'
import styleWeb from '../../styles/web/MusicPlayer/style'

export default function MusicPlayer(){
  const [searchQuery, setSearchQuery] = useState('');
  const onChangeSearch = query => setSearchQuery(query);
  const [sound, setSound] = React.useState();
  async function playSound() {
    console.log('Loading Sound');
    const { sound } = await Audio.Sound.createAsync({uri: 'http://docs.google.com/uc?export=open&id=1xf0MuhO7yOmFe81FFjDwcvb25xV14dKk'}, {shouldPlay:true});
    setSound(sound);

    console.log('Playing Sound');
    await sound.playAsync(); }

  /*{Web app}*/
  if(Platform.OS === 'web'){
    return(
      <View style={styleWeb.navbar}>
        <View>
          <NavBar/>
        </View>
        <View style = {{flex:1}}>
          <ImageBackground source={require('../../../assets/player.png')} style={styleWeb.image}>
            <View style ={styleWeb.tocador}>
              <Text style={styleWeb.title}>Música em Execução</Text>
              <View style = {{paddingTop:40}}/>
              <Image source = {require('../../../assets/noTimeToDie.png')} style={styleWeb.imageplayer}/>
              <View style = {{paddingTop:40}}/>
              <Text style={styleWeb.musicname}>No Time to Die</Text>
              <View style = {{paddingTop:20}}/>
              <Text style= {{textAlign:'center', padding:20}}>"No Time to Die" is a song by American singer and songwriter Billie Eilish. It is the theme song for the upcoming James Bond film of the same name, and was released through Darkroom and Interscope Records on February 13, 2020.[1] The song was written by Eilish and her brother Finneas O'Connell, and recorded in a bedroom studio.[2][3] At age 18, Eilish is the youngest artist to have written and recorded a James Bond theme song.[4][5] The song won the Grammy Award for Best Song Written for Visual Media at the 63rd Annual Grammy Awards.</Text>
              <View style={styleWeb.buttons}>
                <TouchableOpacity>
                  <Image source = {require('../../../assets/back.png')} style={styleWeb.playerbuttonsmini}/>
                </TouchableOpacity>
                <TouchableOpacity>
                  <Image source = {require('../../../assets/playButton.png')} style={styleWeb.playerbuttons}/>
                </TouchableOpacity>
                <TouchableOpacity>
                  <Image source = {require('../../../assets/next.png')} style={styleWeb.playerbuttonsmini}/>
                </TouchableOpacity>
              </View>
            </View>
          </ImageBackground>
        </View>
      </View>
    );
  }
  /*{Mobile app}*/
  else if(Platform.OS === 'android' || Platform.OS === 'ios'){
  }
}

/*{Data style}*/
createTheme('SpotPer', {
  text: {
    primary: '#374754',
    secondary: '#374754',
  },
  background: {
    default: '#E3E1E1',
  },
  context: {
    background: '#cb4b16',
    text: '#FFFFFF',
  },
  divider: {
    default: '#073642',
  }
});

const customStyles = {
  rows: {
    style: {
      minHeight: '55px'
    }
  },
  headCells: {
    style: {
      paddingLeft:'30px'
    },
  },
  cells: {
    style: {
      paddingLeft: '8px',
      paddingRight: '8px'
    },
  },
};
